package org.jboss.webbeans.xml;import java.io.IOException;import java.io.InputStream;import java.lang.annotation.Annotation;import java.net.URL;import java.util.ArrayList;import java.util.Collections;import java.util.List;import javax.enterprise.inject.InjectionException;import javax.xml.parsers.DocumentBuilder;import javax.xml.parsers.DocumentBuilderFactory;import javax.xml.parsers.ParserConfigurationException;import org.jboss.webbeans.DeploymentException;import org.jboss.webbeans.resources.spi.ResourceLoader;import org.jboss.webbeans.resources.spi.ResourceLoadingException;import org.jboss.webbeans.util.dom.NodeListIterable;import org.w3c.dom.Document;import org.w3c.dom.Element;import org.w3c.dom.Node;import org.w3c.dom.Text;import org.xml.sax.SAXException;/** * Simple parser for beans.xml *  * @author Pete Muir * */public class BeansXmlParser{      private static class XmlElement   {      private URL file;      private Element element;            public XmlElement(URL file, Element element)      {         super();         this.file = file;         this.element = element;      }            public URL getFile()      {         return file;      }            public Element getElement()      {         return element;      }            @Override      public String toString()      {         return "File: " + getFile() + "; Node: " + getElement();      }         }      private final Iterable<URL> beansXml;   private final ResourceLoader resourceLoader;      private List<Class<? extends Annotation>> enabledDeploymentTypes;   private List<Class<?>> enabledDecoratorClasses;   private List<Class<?>> enabledInterceptorClasses;      public List<Class<? extends Annotation>> getEnabledDeploymentTypes()   {      return enabledDeploymentTypes;   }      public List<Class<?>> getEnabledDecoratorClasses()   {      return enabledDecoratorClasses;   }      public List<Class<?>> getEnabledInterceptorClasses()   {      return Collections.emptyList();   }      public BeansXmlParser(ResourceLoader resourceLoader, Iterable<URL> beansXml)   {      this.beansXml = beansXml;      this.resourceLoader = resourceLoader;   }      public void parse()   {      DocumentBuilder documentBuilder;      try      {         documentBuilder = DocumentBuilderFactory.newInstance().newDocumentBuilder();      }      catch (ParserConfigurationException e)      {         throw new InjectionException("Error configuring XML parser", e);      }      List<XmlElement> deployElements = new ArrayList<XmlElement>();       List<XmlElement> decoratorElements = new ArrayList<XmlElement>();       List<XmlElement> interceptorElements = new ArrayList<XmlElement>();       for (URL url : beansXml)      {         InputStream is;         boolean fileHasContents;         try         {            is = url.openStream();            fileHasContents = is.available() > 0;         }         catch (IOException e)         {            throw new InjectionException("Error loading beans.xml " + url.toString(), e);         }         if (fileHasContents)         {            Document document;            try            {               document = documentBuilder.parse(is);               document.normalize();            }            catch (SAXException e)            {               throw new DeploymentException("Error parsing beans.xml " + url.toString(), e);            }            catch (IOException e)            {               throw new DeploymentException("Error loading beans.xml " + url.toString(), e);            }            Element beans = document.getDocumentElement();            for (Node child : new NodeListIterable(beans.getChildNodes()))            {               if (child instanceof Element && "deploy".equals(child.getNodeName()))               {                  deployElements.add(new XmlElement(url, (Element) child));               }               if (child instanceof Element && "interceptors".equals(child.getNodeName()))               {                  interceptorElements.add(new XmlElement(url, (Element) child));               }               if (child instanceof Element && "decorators".equals(child.getNodeName()))               {                  decoratorElements.add(new XmlElement(url, (Element) child));               }            }         }      }            if (deployElements.size() > 1)      {         throw new DeploymentException("<deploy> can only be specified once, but it is specified muliple times " + deployElements);      }      else if (deployElements.size() == 1)      {         enabledDeploymentTypes = new ArrayList<Class<? extends Annotation>>();         enabledDeploymentTypes.addAll(processDeployElement(resourceLoader, deployElements.get(0)));      }            if (decoratorElements.size() > 1)      {         throw new DeploymentException("<decorator> can only be specified once, but it is specified muliple times " + decoratorElements);      }      else if (decoratorElements.size() == 1)      {         enabledDecoratorClasses = new ArrayList<Class<?>>();         enabledDecoratorClasses.addAll(processElement(resourceLoader, decoratorElements.get(0)));      }            if (interceptorElements.size() > 1)      {         throw new DeploymentException("<interceptor> can only be specified once, but it is specified muliple times " + interceptorElements);      }      else if (interceptorElements.size() == 1)      {         enabledInterceptorClasses = new ArrayList<Class<?>>();         enabledInterceptorClasses.addAll(processElement(resourceLoader, interceptorElements.get(0)));      }         }      private static List<Class<? extends Annotation>> processDeployElement(ResourceLoader resourceLoader, XmlElement element)   {      List<Class<? extends Annotation>> list = new ArrayList<Class<? extends Annotation>>();      for (Node child : new NodeListIterable(element.getElement().getChildNodes()))      {         String className = processNode(child);         if (className != null)         {            try            {               list.add(resourceLoader.classForName(className).asSubclass(Annotation.class));            }            catch (ResourceLoadingException e)            {               throw new DeploymentException("Cannot load class " + className + " defined in " + element.getFile().toString());            }         }      }      return list;   }      private static String processNode(Node node)   {      if (node instanceof Element)      {         if (node.getChildNodes().getLength() == 1 && node.getChildNodes().item(0) instanceof Text)         {            String className = ((Text) node.getChildNodes().item(0)).getData();            return className;         }      }      return null;   }      private static List<Class<?>> processElement(ResourceLoader resourceLoader, XmlElement element)   {      List<Class<?>> list = new ArrayList<Class<?>>();      for (Node child : new NodeListIterable(element.getElement().getChildNodes()))      {         String className = processNode(child);         if (className != null)         {            try            {               list.add(resourceLoader.classForName(className));            }            catch (ResourceLoadingException e)            {               throw new DeploymentException("Cannot load class " + className + " defined in " + element.getFile().toString());            }         }      }      return list;   }   }